	@RequestMapping("/list")
	public String list(
			@RequestParam(value="page",defaultValue="1") Integer page,
			@ModelAttribute SearchBusinessVoucher s,
			HttpSession session,
			HttpServletRequest request,
			Model model) {
		
		PageCustom<BusinessVoucher> listPage = voucherUserService.filterAdmin(s, page, PAGE_SIZE);
		if(s.getBusinessType() != null){
			List<Integer> listYear = businessSubService.getListManageYearByType(s.getBusinessType());
			model.addAttribute("listYear", listYear);
			if(s.getManagerYear()!= null){
				List<BusinessSub> listBusiness = businessSubService.listPagingByManagerYearAndBusinessType(s.getManagerYear(), s.getBusinessType());
				model.addAttribute("listBusiness", listBusiness);
			}
		}
		
		Calendar c = Calendar.getInstance();
		model.addAttribute(MAIN_LIST_NAME, listPage);	
		model.addAttribute("s", s);
		model.addAttribute("yearNow", c.get(Calendar.YEAR));
		model.addAttribute("activePage", nav);
		new Pager<BusinessVoucher>(listPage).setManualSetting(model, request);
		return JSP_LIST;
	}
	
