/**
 * Spring MVC controller for 'VoucherUser' management.
 */
@Controller
@RequestMapping("/admin/payment_voucher/heart_voucher")
public class HeartVoucherAdminController extends AbstractController {

	//--- Variables names ( to be used in JSP with Expression Language )
	private static final String MAIN_ENTITY_NAME = "heartVoucher";
	private static final String MAIN_LIST_NAME = "list";

	private static final Integer PAGE_SIZE   = 30;

	private static String nav = "voucherUser";

	//--- JSP pages names ( View name in the MVC model )
	private static final String JSP_LIST   = "admin/payment_voucher/heart_voucher/list";

	//--- Main entity service
	@Resource
    private VoucherUserService voucherUserService; // Injected by Spring
	//--- Other service(s)
	@Resource
    private VoucherService voucherService; // Injected by Spring
	@Resource
    private BusinessSubService businessSubService;

	//--------------------------------------------------------------------------------------
	/**
	 * Default constructor
	 */
	public HeartVoucherAdminController() {
		super(HeartVoucherAdminController.class, MAIN_ENTITY_NAME );
		log("VoucherUserAdminController created.");
	}

	@RequestMapping(value = "/list", method = RequestMethod.GET)
	public String list(
			@RequestParam(value="page",defaultValue="1") Integer page,
			@ModelAttribute SearchHeartVoucher s,
			HttpSession session,
			HttpServletRequest request,
			Model model) {
		PageCustom<BusinessVoucher> listPage = voucherUserService.filterHeartVoucher(s, page, PAGE_SIZE);
		Voucher v = voucherService.findByTypeHeart(ApplicationDefine.Voucher_Type.HeartVoucher.getCode());
		Calendar c = Calendar.getInstance();
		if(v.getTypeTime() == 2){
			model.addAttribute("dateNumber", v.getDateNumber());
		}else{
			model.addAttribute("dateNumber", DataUtils.diffirentTwoDate(v.getFromTime(), v.getToTime()));
		}
		model.addAttribute(MAIN_LIST_NAME, listPage);	
		model.addAttribute("s", s);
		model.addAttribute("v", v);
		model.addAttribute("now", c.getTime());
		model.addAttribute("activePage", nav);
		new Pager<BusinessVoucher>(listPage).setManualSetting(model, request);
		return JSP_LIST;
	}
	
	@RequestMapping(value = "/list", method = RequestMethod.POST)
	public String list_post(
			@RequestParam(value="page",defaultValue="1") Integer page,
			@ModelAttribute Voucher v,
			HttpSession session,
			HttpServletRequest request,
			Model model) {
		Voucher voucher = voucherService.findByTypeHeart(ApplicationDefine.Voucher_Type.HeartVoucher.getCode());
		voucher.setFee(v.getFee());
		voucher.setHeartNumber(v.getHeartNumber());
		voucher.setDateNumber(v.getDateNumber());
		voucher.setUpdateDate(new Date());
		voucherService.update(voucher);
		return "redirect:/"+ JSP_LIST;
	}
	
}
