	@RequestMapping("/list")
	public String user_chart(
			HttpSession session,
			HttpServletRequest request,
			@RequestParam(value="page",defaultValue="1") Integer page,
			@ModelAttribute SearchStatistics s,
			Model model) {
		Date now = new Date();
		PageCustom<ServiceStatistics> listPage = shareHistoryService.filterStatistics(s);
		model.addAttribute("now", now);
		model.addAttribute("s", s);
		model.addAttribute("total", listPage.getOther());
		new Pager<ServiceStatistics>(listPage).setManualSetting(model, request);
		return JSP_LIST;
	}

