	@RequestMapping("/user/chart")
	public String user_chart(
			HttpSession session,
			HttpServletRequest request,
			@RequestParam(value="page",defaultValue="1") Integer page,
			@ModelAttribute SearchStatistics s,
			Model model) {
		Calendar cal = Calendar.getInstance();
		cal.add(Calendar.DATE, -1);
		MemberStatisticsChart m = userService.getUserStatisticsChart(cal.getTime());
		PageCustom<UserStatistics> listPage = userService.filterMemberStatistics(s, page, PAGE_SIZE);
		model.addAttribute("now", cal.getTime());
		model.addAttribute("chart", m);
		model.addAttribute("s", s);
		model.addAttribute("total", listPage.getOther());
		new Pager<UserStatistics>(listPage).setManualSetting(model, request);
		return JSP_USER_CHART;
	}
	
