	@RequestMapping("/mindcheck")
	public String user_chart(
			HttpSession session,
			HttpServletRequest request,
			@RequestParam(value="version",defaultValue="") String version,
			@ModelAttribute SearchStatistics s,
			Model model) {
		Date now = new Date();
		List<QuestionnaireEntity> listVersion = questionnaireService.listByType(ApplicationDefine.Questionnaire_Type.MindCheck.getCode());
		if(version == null){
			s.setVersion(listVersion.get(0).getVersion());
		}
		PageCustom<MindCheckStatistics> listPage = testResultService.filterMindCheckStatistics(s);
		model.addAttribute("now", now);
		model.addAttribute("s", s);
		model.addAttribute("listVersion", listVersion);
		new Pager<MindCheckStatistics>(listPage).setManualSetting(model, request);
		return JSP_MINDCHECK;
	}

