	@RequestMapping("/psychological/download")
	public void psychological_download(
			HttpSession session,
			HttpServletRequest request,
			@ModelAttribute SearchStatistics s,
			HttpServletResponse response,
			Model model) {
		PageCustom<PsychologicalStatistics> listPage = testResultService.filterPsychologicalStatistics(s);
		List<Object> listOther = listPage.getOther();
		System.out.println("size:"+listPage.getList().size());
		try {
			List<Object> header = new ArrayList<>();
			header.add(listOther.get(0));
			header.add(listOther.get(1));
			if(listOther.get(0).equals(0)){
				header.add("0%");
			}else{
				header.add("100%");
			}
			List<List<Object>> list_data = null;
			if (listPage.getList() != null) {
				list_data = new ArrayList<>();
				Integer totalRequest = Integer.valueOf(listOther.get(0).toString());
				for (PsychologicalStatistics i : listPage.getList()) {
					List<Object> list_str = new ArrayList<Object>();
					list_str.add(i.getTestName());
					list_str.add(i.getCompany());
					list_str.add(i.getPayment());
					list_str.add(i.getRequest());
					if(totalRequest.equals(0)){
						list_str.add("0%");
					}else{
						list_str.add(Math.round((i.getPayment()/(float)totalRequest)*100)+"%");
					}
					
					list_data.add(list_str);
				}
			}
			ExcelUtils.createExcelFileUseTemplateHeaderCustom(request, response, ApplicationDefine.ExcelTemplate.PSYCHOLOGICAL_STATISTIC, header, list_data);
		} catch (Exception e) {
			e.printStackTrace();
		}
	}
